import { HttpException, HttpStatus, Injectable } from '@nestjs/common';
import { JwtService } from '@nestjs/jwt';
import * as bcrypt from 'bcrypt';
import { CustomerService } from '../customer/customer.service';
import { JwtPayload, SignInUser, SignUpUser } from './interfaces/jwt-payload.interface';

const saltRounds = 6;

@Injectable()
export class AuthService {
    constructor(private readonly customerService: CustomerService, private readonly jwtService: JwtService) {
    }

    async validateUser(payload: JwtPayload): Promise<any> {
        const { email } = payload;
        return await this.customerService.findOneByEmail({ email });
    }

    async signUp({ email, name, password }: SignUpUser) {

        const user = await this.customerService.findOneByEmail({ email });
        if (user) {
            throw new HttpException(`User with ${email} already exist.`, HttpStatus.BAD_REQUEST);
        }

        const token = this.jwtService.sign({ email });
        const encryptPass = await await bcrypt.hash(password, saltRounds);
        const saveCustomer = await this.customerService.saveCustomer({ name, email, password: encryptPass });
        return { token, ...saveCustomer };
    }

    async signIn({ email, password }: SignInUser): Promise<any> {
        const user = await this.customerService.findOneByEmail({ email });
        if (!user) {
            throw new HttpException(`User with ${email} are not registered.`, HttpStatus.BAD_REQUEST);
        }

        const match = await bcrypt.compare(password, user.password);
        if (!match) {
            throw new HttpException(`Email or Password is invalid`, HttpStatus.BAD_REQUEST);
        }

        const token = this.jwtService.sign({ email });
        return { token, ...user };
    }

}
